    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Zaloguj się do Markusa`{% endblock %}

{% block body %}
<style>
    form h1{text-align:center;}
    input{
        width:50% !important;
        position:relative;
        left:25%;
    }
    label{
        position:relative;
        left:49%;
    }
    button[type=submit]{
        width:50%;
        position:relative;
        left:25%;
    }
</style>
<h1>Markus.pl</h1>
{% include \"_partials/menu.html.twig\" %}
<form method=\"post\">

    {% if error %}
        <div class=\"alert alert-danger\">Nieprawidłowy login i/lub hasło</div>
    {% endif %}

    <h1 class=\"h3 mb-3 font-weight-normal\">Zaloguj się</h1>
    <label for=\"inputEmail\">Email</label>
    <input type=\"email\" value=\"{{ last_username }}\" name=\"email\" id=\"inputEmail\" class=\"form-control\" autocomplete=\"email\" placeholder=\"Email\" required autofocus><br />
    <label for=\"inputPassword\">Hasło</label>
    <input type=\"password\" name=\"password\" id=\"inputPassword\" class=\"form-control\" autocomplete=\"current-password\" placeholder=\"Hasło\" required><br />

    <input type=\"hidden\" name=\"_csrf_token\" value=\"{{ csrf_token('authenticate') }}\">

    {#
        Uncomment this section and add a remember_me option below your firewall to activate remember me functionality.
        See https://symfony.com/doc/current/security/remember_me.html

        <div class=\"checkbox mb-3\">
            <label>
                <input type=\"checkbox\" name=\"_remember_me\"> Remember me
            </label>
        </div>
    #}

    <button class=\"btn btn-lg btn-primary\" type=\"submit\">
        Zaloguj
    </button>
</form>
{% endblock %}
", "security/login.html.twig", "/var/www/markus/templates/security/login.html.twig");
